<?phpnamespace app\models;use yii\base\Model;use SoapClient;class EntryForm extends Model{    public $sity;    public $weight;    public $price;    public function rules()    {        return [            [['sity', 'weight'], 'required', 'message'=>'Обязательное поле для заполнения'],//            ['sity', 'weight'],            ['weight', 'double', 'max' => 100000],        ];    }    public function apisoap($apiplace){        ini_set("soap.wsdl_cache_enabled", "0");	$clientS = new SoapClient($apiplace,        array('features' => SOAP_SINGLE_ELEMENT_ARRAYS));	$s=array();	$s['token']='11570.pbpqebfc';	try {$data=$clientS->ListCities($s);}	catch (SoapFault $exception){$errS=$exception;}	if(@$errS){echo $errS;} // произошла ошибка, выводим её            else            {                return $data;            }    }        public function apisoapc($apiplace, $w, $t){        ini_set("soap.wsdl_cache_enabled", "0");        $clientS = new SoapClient($apiplace,        array('features' => SOAP_SINGLE_ELEMENT_ARRAYS));        $s=array();        $s['token']='11570.pbpqebfc';        $s['weight']=$w;        $s['target']=$t;        try {$data=$clientS->DeliveryCosts($s);}        catch (SoapFault $exception){$errS=$exception;}        if(@$errS){echo $errS;} // произошла ошибка, выводим её            else            {                return $data;            }	} 	public function attributeHints(){		return [ 'вес' => 'Вес не более 100000 грамм', 'город' => 'Проверочное сообщение' ];	} /*    public function apiconvert($apiconv){        if(is_object($apiconv)){        $apiconv = get_object_vars($apiconv);        }        if(is_array($apiconv)){        return array_map(__FUNCTION__, $apiconv);        }            else {                 return $apiconv;                }    }*/}